import React, { useReducer, useState } from 'react';
import './style.css';
import DigitButton from './digit';
 export const ACTIONS = { 
  ADD_DIGITS: 'add-digit',
  CHOOSE_OPERATION: 'choose operation',
  CLEAR: 'clear',
  DELETE_DIGIT: 'delete-digit',
  EVALUATE: 'evalute'
}
function reducer(state, { type, payload}) {
  switch(type) {
    case ACTIONS.ADD_DIGITS:
     return state 
    }

}
function App()
{
  const [{ currentOpernad, previousOperand, operation}, dispatch] = useReducer(
    reducer,
    {}
  )
  dispatch({ type: ACTIONS.ADD_DIGITS, payload: {digit: 1} } )
  return (

    <div className="calculator-grid">
      <div className="output">
        <div className="previous-operand"></div>
        <div className="current-operand"></div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <operationButton digit="/" dispatch={dispatch} />
      <DigitButton digit="1" dispatch={dispatch} />
      <button>2</button>
      <button>3</button>
      <button>*</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>+</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>
      <button>-</button>
      <button>.</button>
      <button>0</button>
      <button className="span-two">=</button>

     </div>

      )
      }

export default App;
